import { useEffect, useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import {
  featuredPortfolio,
  webPortfolio,
  mobilePortfolio,
  designPortfolio,
  contentPortfolio,
} from "../../data";

export default function Portfolio() {
  const [selected, setSelected] = useState("featured");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "featured",
      title: "Featured",
    },
    {
      id: "web",
      title: "Web App",
    },
    {
      id: "mobile",
      title: "Mobile App",
    },
    {
      id: "design",
      title: "Design",
    },
    {
      id: "content",
      title: "Content",
    },
  ];

  useEffect(() => {
    switch (selected) {
      case "featured":
        setData(featuredPortfolio);
        break;
      case "web":
        setData(webPortfolio);
        break;
      case "mobile":
        setData(mobilePortfolio);
        break;
      case "design":
        setData(designPortfolio);
        break;
      case "content":
        setData(contentPortfolio);
        break;
      default:
        setData(featuredPortfolio);
    }
  }, [selected]);

  return (
    <div className="resume-grid">
          <div className="resume-experience">
            <h3 className="resume-heading">Experience</h3>
            <div className="resume-item">
              <h5 className="resume-title">Discord</h5>
              <h6 className="resume-caption">
                <div className="resume-role">Senior Product Designer<div className="dot"></div></div>
                <div className="resume-timestamp">2018 - Now</div>
              </h6>
            </div>
            <div className="resume-item">
              <h5 className="resume-title">Blitz</h5>
              <h6 className="resume-caption">
                <div className="resume-role">Lead Designer<div className="dot"></div></div>
                <div className="resume-timestamp">2015 - 2018</div>
              </h6>
            </div>
            <div className="resume-item">
              <h5 className="resume-title">Box</h5>
              <h6 className="resume-caption">
                <div className="resume-role">Product Designer<div className="dot"></div></div>
                <div className="resume-timestamp">2015</div>
              </h6>
            </div>
            <div className="resume-item">
              <h5 className="resume-title">Iris</h5>
              <h6 className="resume-caption">
                <div className="resume-role">Founder and Designer<div className="dot"></div></div>
                <div className="resume-timestamp">2012 - 2014</div>
              </h6>
            </div>
          </div>
          <div className="resume-featured">
            <h3 className="resume-heading">Featured in</h3>
            <div className="resume-item">
              <h5 className="resume-title">San Francisco Chronicle</h5>
              <h6 className="resume-caption">
                <div className="resume-role"><a href="https://www.sfchronicle.com/bayarea/article/Can-you-catch-coronavirus-swimming-What-about-15260424.php" target="_blank">Debunking myths with COVID-19 Fact Check</a></div>
                <div className="resume-timestamp">May 2020</div>
              </h6>
            </div>
            <div className="resume-item">
              <h5 className="resume-title">Fast Company</h5>
              <h6 className="resume-caption">
                <div className="resume-role"><a href="https://www.fastcompany.com/90236684/why-you-should-redesign-your-portfolio-every-yeara" target="_blank">Why you should redesign your portfolio every year</a></div>
                <div className="resume-timestamp">Sep 2018</div>
              </h6>
            </div>
            <div className="resume-item">
              <h5 className="resume-title">Awwwards</h5>
              <h6 className="resume-caption">
                <div className="resume-role"><a href="https://www.awwwards.com/sites/design-by-roka" target="_blank">Designbyroka, Honorable Mention</a></div>
                <div className="resume-timestamp">Sep 2018</div>
              </h6>
            </div>
            <div className="resume-item">
              <h5 className="resume-title">Product Hunt</h5>
              <h6 className="resume-caption">
                <div className="resume-role"><a href="https://www.producthunt.com/posts/witeboard" target="_blank">Witeboard, Product of the Month</a></div>
                <div className="resume-timestamp">Feb 2018</div>
              </h6>
            </div>
          </div>
          </div>
          /*
    <div className="portfolio" id="portfolio">
      <h1> Experience & Portfolio</h1>
      <ul>
        {list.map((item) => (
          <PortfolioList
            title={item.title}
            active={selected === item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        {data.map((d) => (
          <div className="item">
            <img
              src={d.img}
              alt=""
            />
            <h3>{d.title}</h3>
          </div>
        ))}
      </div>
    </div>*/
  );
}
